column.remarks:
The number of steps
required to get from the subject cvterm to the object cvterm, counting
from zero (reflexive relationship)

table.remarks:
The table stockcollectionprop
contains the value of the stock collection such as website/email URLs;
the value of the stock collection order URLs.

routine.definition:
BEGIN
NEW.abstract_tsvector := to_tsvector(NEW.abstract);
RETURN NEW;
END

DECLARE
		maprow RECORD;
		querystr TEXT;
		queries TEXT[];
		emptyquery TEXT;
		matviewquery TEXT;
	BEGIN

		-- Remove exsiting materialized view, if it exists
		DROP MATERIALIZED VIEW IF EXISTS public.materialized_markerview;

		-- Get the unique species / reference genome combinations from the nd_protocolprop table
		FOR maprow IN (
			SELECT value->>'species_name' AS species,
				concat(
					substring(split_part(value->>'species_name', ' ', 1), 1, 1),
					substring(split_part(value->>'species_name', ' ', 2), 1, 1)
				) AS species_abbreviation,
				value->>'reference_genome_name' AS reference_genome,
				replace(replace(value->>'reference_genome_name', '_', ''), ' ', '') AS reference_genome_cleaned
			FROM nd_protocolprop 
			WHERE type_id = (SELECT cvterm_id FROM public.cvterm WHERE name = 'vcf_map_details')
			GROUP BY species, reference_genome
		)

		-- Loop through each unique combination of species / reference genome and build the marker query
		LOOP
			querystr := 'SELECT nd_protocolprop.nd_protocol_id, ''' || maprow.species || ''' AS species_name, ''' || maprow.reference_genome || ''' AS reference_genome_name, s.value->>''name'' AS marker_name, s.value->>''chrom'' AS chrom, cast(coalesce(nullif(s.value->>''pos'',''''),NULL) as numeric) AS pos, s.value->>''ref'' AS ref, s.value->>''alt'' AS alt, CASE WHEN s.value->>''alt'' < s.value->>''ref'' THEN concat(''' || maprow.species_abbreviation || ''', ''' || maprow.reference_genome_cleaned || ''', ''_'', REGEXP_REPLACE(s.value->>''chrom'', ''^chr?'', ''''), ''_'', s.value->>''pos'', ''_'', s.value->>''alt'', ''_'', s.value->>''ref'') ELSE concat(''' || maprow.species_abbreviation || ''', ''' || maprow.reference_genome_cleaned || ''', ''_'', REGEXP_REPLACE(s.value->>''chrom'', ''^chr?'', ''''), ''_'', s.value->>''pos'', ''_'', s.value->>''ref'', ''_'', s.value->>''alt'') END AS variant_name FROM nd_protocolprop, LATERAL jsonb_each(nd_protocolprop.value) s(key, value) WHERE type_id = (SELECT cvterm_id FROM public.cvterm WHERE name = ''vcf_map_details_markers'') AND nd_protocol_id IN (SELECT nd_protocol_id FROM nd_protocolprop WHERE value->>''species_name'' = ''' || maprow.species || ''' and value->>''reference_genome_name'' = ''' || maprow.reference_genome || ''' AND type_id = (SELECT cvterm_id FROM public.cvterm WHERE name = ''vcf_map_details''))';
			queries := array_append(queries, querystr);

		END LOOP;

		-- Add an empty query in case there is no existing marker data
		emptyquery := 'SELECT column1::int AS nd_protocol_id, column2::text AS species_name, column3::text AS reference_genome_name, column4::text AS marker_name, column5::text AS chrom, column6::numeric AS pos, column7::text AS ref, column8::text AS alt, column9::text AS variant_name FROM (values (null,null,null,null,null,null,null,null,null)) AS x WHERE false';
		queries := array_append(queries, emptyquery);

		-- Combine queries with a UNION
		matviewquery := array_to_string(queries, ' UNION ');

		-- Build the materialized view
		EXECUTE 'CREATE MATERIALIZED VIEW public.materialized_markerview AS (' || matviewquery || ') WITH NO DATA';
		ALTER MATERIALIZED VIEW public.materialized_markerview OWNER TO web_usr;

		-- Add indexes
		CREATE INDEX materialized_markerview_idx1 ON public.materialized_markerview(nd_protocol_id);
		CREATE INDEX materialized_markerview_idx2 ON public.materialized_markerview(species_name);
		CREATE INDEX materialized_markerview_idx3 ON public.materialized_markerview(reference_genome_name);
		CREATE INDEX materialized_markerview_idx4 ON public.materialized_markerview(marker_name);
		CREATE INDEX materialized_markerview_idx5 ON public.materialized_markerview(UPPER(marker_name));
		CREATE INDEX materialized_markerview_idx6 ON public.materialized_markerview(chrom);
		CREATE INDEX materialized_markerview_idx7 ON public.materialized_markerview(pos);
		CREATE INDEX materialized_markerview_idx8 ON public.materialized_markerview(variant_name);
		CREATE INDEX materialized_markerview_idx9 ON public.materialized_markerview(UPPER(variant_name));
		CREATE INDEX materialized_markerview_idx10 ON public.materialized_markerview USING GIN(marker_name gin_trgm_ops);
		CREATE INDEX materialized_markerview_idx11 ON public.materialized_markerview USING GIN(variant_name gin_trgm_ops);

		-- Refresh materialzied view, if requested with function argument
		IF $1 THEN
			EXECUTE 'REFRESH MATERIALIZED VIEW public.materialized_markerview';
		END IF;

		-- Return true if the materialized view is refreshed
		RETURN $1;

	END

DECLARE
  return_string      varchar;
  f_id               ALIAS FOR $1;
  atts_view          gffatts%ROWTYPE;
  feature_row        feature%ROWTYPE;
  name               varchar;
  uniquename         varchar;
  parent             varchar;
  escape_loc         int; 
BEGIN
  --Get name from feature.name
  --Get ID from feature.uniquename

  SELECT INTO feature_row * FROM feature WHERE feature_id = f_id;
  name  = feature_row.name;
  return_string = 'ID=' || feature_row.uniquename;
  IF name IS NOT NULL AND name != ''
  THEN
    return_string = return_string ||';' || 'Name=' || name;
  END IF;

  --Get Parent from feature_relationship
  SELECT INTO feature_row * FROM feature f, feature_relationship fr
    WHERE fr.subject_id = f_id AND fr.object_id = f.feature_id;
  IF FOUND
  THEN
    return_string = return_string||';'||'Parent='||feature_row.uniquename;
  END IF;

  FOR atts_view IN SELECT * FROM gff3atts WHERE feature_id = f_id  LOOP
    escape_loc = position(';' in atts_view.attribute);
    IF escape_loc > 0 THEN
      atts_view.attribute = replace(atts_view.attribute, ';', '%3B');
    END IF;
    return_string = return_string || ';'
                     || atts_view.type || '='
                     || atts_view.attribute;
  END LOOP;

  RETURN return_string;
END;

DECLARE
  myrec RECORD;
  stmt VARCHAR;
  query VARCHAR;
  bt_schema VARCHAR;
BEGIN
  bt_schema = scm || '_bt';
  query = 'SELECT relname 
             FROM pg_class c, pg_namespace n
            WHERE c.relnamespace=n.oid 
              AND c.relkind=''r''
              AND n.nspname=''' || bt_schema || '''';
--  RAISE NOTICE '%', query; --stmt;
  FOR myrec IN EXECUTE query LOOP
    RAISE NOTICE 'Making rows in ''%'' visible', myrec.relname;
    stmt = 'SELECT make_all_rows_visible(''' || scm || '''::varchar, ''' || myrec.relname || '''::varchar)';
    --RAISE NOTICE '%', stmt;
    EXECUTE stmt;
  END LOOP;
  RETURN 't';
END

DECLARE
    paccession           ALIAS FOR $1;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid feature_id
      FROM feature
      WHERE feature.accession = paccession;
    IF NOT FOUND THEN
      INSERT INTO feature 
       (accession)
         VALUES
       (paccession);
      RETURN currval('feature_pk_seq');
    END IF;
    UPDATE feature
      SET

      WHERE
         feature.accession = paccession

    RETURN pkeyid;
  END;

DECLARE
    pname                ALIAS FOR $1;
    paccession           ALIAS FOR $2;
    pfmin                ALIAS FOR $3;
    pfmax                ALIAS FOR $4;
    pfstrand             ALIAS FOR $5;
    presidues            ALIAS FOR $6;
    pseqlen              ALIAS FOR $7;
    pmd5checksum         ALIAS FOR $8;
    ptype_id             ALIAS FOR $9;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid feature_id
      FROM feature
      WHERE feature.name = pname AND
            feature.accession = paccession AND
            feature.fmin = pfmin AND
            feature.fmax = pfmax AND
            feature.fstrand = pfstrand AND
            feature.residues = presidues AND
            feature.seqlen = pseqlen AND
            feature.md5checksum = pmd5checksum AND
            feature.type_id = ptype_id;
    IF NOT FOUND THEN
      INSERT INTO feature 
       (name, accession, fmin, fmax, fstrand, residues, seqlen, md5checksum, type_id)
         VALUES
       (pname, paccession, pfmin, pfmax, pfstrand, presidues, pseqlen, pmd5checksum, ptype_id);
      RETURN currval('feature_pk_seq');
    END IF;
    UPDATE feature
      SET

      WHERE
         feature.name = pname AND
            feature.accession = paccession AND
            feature.fmin = pfmin AND
            feature.fmax = pfmax AND
            feature.fstrand = pfstrand AND
            feature.residues = presidues AND
            feature.seqlen = pseqlen AND
            feature.md5checksum = pmd5checksum AND
            feature.type_id = ptype_id

    RETURN pkeyid;
  END;

DECLARE
    pfeature_id          ALIAS FOR $1;
    pcvterm_id           ALIAS FOR $2;
    ppub_id              ALIAS FOR $3;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid feature_cvterm_id
      FROM feature_cvterm
      WHERE feature_cvterm.feature_id = pfeature_id AND
            feature_cvterm.cvterm_id = pcvterm_id AND
            feature_cvterm.pub_id = ppub_id;
    IF NOT FOUND THEN
      INSERT INTO feature_cvterm 
       (feature_id, cvterm_id, pub_id)
         VALUES
       (pfeature_id, pcvterm_id, ppub_id);
      RETURN currval('feature_cvterm_pk_seq');
    END IF;
    UPDATE feature_cvterm
      SET

      WHERE
         feature_cvterm.feature_id = pfeature_id AND
            feature_cvterm.cvterm_id = pcvterm_id AND
            feature_cvterm.pub_id = ppub_id

    RETURN pkeyid;
  END;

DECLARE
    pdbxref_id           ALIAS FOR $1;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid feature_dbxref_id
      FROM feature_dbxref
      WHERE feature_dbxref.feature_dbxref_id = pfeature_dbxref_id AND
            feature_dbxref.dbxref_id = pdbxref_id;
    IF NOT FOUND THEN
      INSERT INTO feature_dbxref 
       (dbxref_id)
         VALUES
       (pdbxref_id);
      RETURN currval('feature_dbxref_pk_seq');
    END IF;
    UPDATE feature_dbxref
      SET

      WHERE
         feature_dbxref.feature_dbxref_id = pfeature_dbxref_id AND
            feature_dbxref.dbxref_id = pdbxref_id

    RETURN pkeyid;
  END;

DECLARE
    psubj_feature_id     ALIAS FOR $1;
    pobj_feature_id      ALIAS FOR $2;
    ptype_id             ALIAS FOR $3;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid feature_relationship_id
      FROM feature_relationship
      WHERE feature_relationship.subj_feature_id = psubj_feature_id AND
            feature_relationship.obj_feature_id = pobj_feature_id AND
            feature_relationship.type_id = ptype_id;
    IF NOT FOUND THEN
      INSERT INTO feature_relationship 
       (subj_feature_id, obj_feature_id, type_id)
         VALUES
       (psubj_feature_id, pobj_feature_id, ptype_id);
      RETURN currval('feature_relationship_pk_seq');
    END IF;
    UPDATE feature_relationship
      SET

      WHERE
         feature_relationship.subj_feature_id = psubj_feature_id AND
            feature_relationship.obj_feature_id = pobj_feature_id AND
            feature_relationship.type_id = ptype_id

    RETURN pkeyid;
  END;

DECLARE
    pfeature_id          ALIAS FOR $1;
    ppkey_id             ALIAS FOR $2;
    ppval                ALIAS FOR $3;
    pprank               ALIAS FOR $4;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid featureprop_id
      FROM featureprop
      WHERE featureprop.feature_id = pfeature_id AND
            featureprop.pkey_id = ppkey_id AND
            featureprop.pval = ppval AND
            featureprop.prank = pprank;
    IF NOT FOUND THEN
      INSERT INTO featureprop 
       (feature_id, pkey_id, pval, prank)
         VALUES
       (pfeature_id, ppkey_id, ppval, pprank);
      RETURN currval('featureprop_pk_seq');
    END IF;
    UPDATE featureprop
      SET

      WHERE
         featureprop.feature_id = pfeature_id AND
            featureprop.pkey_id = ppkey_id AND
            featureprop.pval = ppval AND
            featureprop.prank = pprank

    RETURN pkeyid;
  END;

DECLARE
    pfeatureprop_id      ALIAS FOR $1;
    ppub_id              ALIAS FOR $2;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid 0
      FROM featureprop_pub
      WHERE featureprop_pub.featureprop_id = pfeatureprop_id AND
            featureprop_pub.pub_id = ppub_id;
    IF NOT FOUND THEN
      INSERT INTO featureprop_pub 
       (featureprop_id, pub_id)
         VALUES
       (pfeatureprop_id, ppub_id);
      RETURN currval('0');
    END IF;
    UPDATE featureprop_pub
      SET

      WHERE
         featureprop_pub.featureprop_id = pfeatureprop_id AND
            featureprop_pub.pub_id = ppub_id

    RETURN pkeyid;
  END;

DECLARE
    pname                ALIAS FOR $1;

    pkeyid INTEGER;
  BEGIN
    SELECT INTO pkeyid gene_id
      FROM gene
